const apiKey = "&app_key=5be7b142c0faea901956bd642929df69";
const apiId = "&app_id=1ba68463";
const maxTime = "&time=30";
const maxIngreds = `&ingr=10`;

export default class App extends Component {

  state = {
    meals: []
  }

  componentDidMount(){
    this.getRecipes();
  }

  getRecipes = () => {
    fetch(mealUrl)
      .then(response => {
      if (!response.data.hints.length) {
        return res.send({
          error: 'No food found'
        })
      }
      res.send(JSON.stringify(response.data.hints))
    })
    .catch(error => res.sendStatus(error.response.status))
  }


  render(){
    return (
      <div className="App">
        <h1>Meal Deal</h1>
        <form>
          <input name="q" placeholder="Type for search"/>
          <input type="submit" value="search"/>
        </form>
      </div>
    );
  }
}
