import React, { useState } from "react";
import ReactCardFlip from 'react-card-flip';


export default function RecipeCard ({recipe, addRecipe}) {
    const {strMeal, strInstructions, strYoutube, strMealThumb, strIngredient1, strIngredient2, strIngredient3, strIngredient4, strIngredient5, strIngredient6, strIngredient7, strIngredient8, strIngredient9, strIngredient10, strIngredient11, strIngredient12, strIngredient13, strIngredient14, strIngredient15, strIngredient16, strIngredient17, strIngredient18, strIngredient19, strIngredient20, strMeasure1, strMeasure2, strMeasure3, strMeasure4, strMeasure5, strMeasure6, strMeasure7, strMeasure8, strMeasure9, strMeasure10, strMeasure11, strMeasure12, strMeasure13, strMeasure14, strMeasure15, strMeasure16, strMeasure17, strMeasure18, strMeasure19, strMeasure20} = recipe
    const [isFlipped, setIsFlipped] = useState(false);
    const handleClick = () => {
        setIsFlipped(!isFlipped);
    }

    const handleRecipeToFavorites = (event) => {
        event.stopPropagation();
        console.log(recipe)
        addRecipe(recipe);
    }

    return(
        <ReactCardFlip isFlipped={isFlipped} fliDirection="vertical">
            <div>
                <div className="front-recipe-card" onClick={handleClick}>
                    <button className="add-recipe" onClick={handleRecipeToFavorites}>Add to favorite</button>
                    <h4>{strMeal}</h4>
                    <img className="recipe-card-image" src={strMealThumb} alt=""/>
                </div>
            </div>
            <div>
                <div className="back-recipe-card" onClick={handleClick}>
                    {/* <p>{strInstructions}</p> */}
                    <p>Ingredients: {strIngredient1} {strMeasure1}; 
                    {strIngredient2} {strMeasure2}; 
                    {strIngredient3} {strMeasure3}; 
                    {strIngredient4} {strMeasure4}; 
                    {strIngredient5} {strMeasure5}; 
                    {strIngredient6} {strMeasure6}; 
                    {strIngredient7} {strMeasure7};
                    {strIngredient8} {strMeasure8};
                    {strIngredient9} {strMeasure9};
                    {strIngredient10} {strMeasure10};
                    {strIngredient11} {strMeasure11};
                    {strIngredient12} {strMeasure1};
                    {strIngredient13} {strMeasure1};
                    {strIngredient14} {strMeasure1};
                    {strIngredient15} {strMeasure1};
                    {strIngredient16} {strMeasure1};
                    {strIngredient17} {strMeasure1};
                    {strIngredient18} {strMeasure1};

                    </p>
                    {/* <a href={strYoutube} target="_blank" rel="noopener noreferrer">Go to watch video</a> */}
                </div>
            </div>
        </ReactCardFlip>
    )
}
