import React, { useState, useEffect } from 'react';
import axios from "axios"
import RecipeList from './RecipeList';
import { NavLink } from 'react-router-dom';
import { Navbar, Nav } from "react-bootstrap"

export default function Home ({props}){

  // const apiKey = "5be7b142c0faea901956bd642929df69";
  // const apiId = "1ba68463";

  const [recipe, setRecipe] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('chicken');
  const mealUrl = `https://www.themealdb.com/api/json/v1/1/search.php?s=${query}`;


  useEffect(() => {
   getRecipe()
  }, [query])


  const getRecipe = () => {
      axios.get(mealUrl)
      // .then(response => console.log(response.data.meals))
        .then(response => setRecipe(response.data.meals))
  }

  const getSearch = (event) => {
    setSearch(event.target.value)
  }

  const updateChange = (event) => {
    event.preventDefault();
    setQuery(search)
  }


  return ( 
    <div className="Home">
      <Navbar>
      <Navbar.Brand href="/">Navbar with text</Navbar.Brand>
        <Navbar.Toggle aria-controls="basic-navbar-nav"/>
        <Navbar.Collapse id="basic-navbar-nav"></Navbar.Collapse>
          <Nav>
            <NavLink className="d-inline p-2 bg-dark text-white" to="/">Go To Main Page</NavLink>
            <NavLink className="d-inline p-2 bg-dark text-white" to="/favorites">Favorites</NavLink>
            <form className="d-inline p-2 bg-dark text-white" onSubmit={updateChange}>
              <h3>Search your meal</h3>
              <input type="text" value={search} onChange={getSearch}/>
              <button type="submit" >Search</button>
            </form>
          </Nav>
      </Navbar>
      <div className="meals-list">
        <RecipeList recipes={recipe} />
      </div>
    </div>
  );

}
