import React, { useState, useEffect } from 'react';
import axios from "axios"
import RecipeList from './RecipeList';
import Navbar from "./Navbar";
import ScrollUpButton from "react-scroll-up-button";

export default function Home (props){

  const [recipe, setRecipe] = useState([]);
  const [search, setSearch] = useState('');
  const [query, setQuery] = useState('');
  const [alert, setAlert] = useState('');
  const mealUrl = `https://www.themealdb.com/api/json/v1/1/search.php?s=${query}`;

  useEffect(() => {
   getRecipe()
  }, [query])


  const getRecipe = () => {
      axios.get(mealUrl)
        // .then(response => response.json())
        .then(result => {
          if(result === null){
            setAlert("No such result")
          } else {
            setRecipe(result.data.meals)
          }
        })
  }

  const getSearch = (event) => {
    setSearch(event.target.value)
  }

  const updateChange = (event) => {
    event.preventDefault();
    setQuery(search)
  }


  return ( 
    <div className="Home">
      <Navbar />
      <div className="meals-list">
            <form className="search-form" onSubmit={updateChange}>
              <h3>Search your meal</h3>
              <input type="text" value={search} onChange={getSearch}/>
              <button type="submit" >Search</button>
            </form>
        { alert ? <p>{alert}</p> : null}
        <RecipeList recipes={recipe} {...props}/>
        <ScrollUpButton />
      </div>
    </div>
  );

}
