    // const showFavorites = users.filter(user => user.id === +userId)

    console.log(recipes)

    // useEffect(() => {
    //     validateUser()
    //    },)
    const showFavorites = recipes.map((recipe, i) => <FavoriteItem key={i} {...recipe} deleteFavorite={deleteFavorite}/>)

    return(
        <>
                <h3>Your favorite meals</h3>
                <li className="go-back-link"><NavLink to="/">Go Back</NavLink></li>
            <div className="favorites-list"> 
                {showFavorites}
                {/* <FavoriteItem user = {user} favorites={favorites} deleteFavorite={deleteFavorite}/> */}
            </div>
        </>
    )

}
