import React, { Component } from 'react';
import MealList from "./components/MealList"
import './App.css';

const mealUrl = ""

export default class App extends Component {

  state = {
    meals: []
  }

  componentDidMount(){
    this.getMeals();
  }

  getMeals = () => {
    fetch(mealUrl, {
      headers: 'Authorization: Token token="8ea3a5a23f6a216745943b67940ee3bb"'
    })
      .then(response => response.json())
      .then(console.log)
    }

  render(){
    return (
      <div className="App">
        <h1>Meal Deal</h1>
        <form>
          <input name="q" placeholder="Type for search"/>
          <input type="submit" value="search"/>
        </form>
      </div>
    );
  }
}
