import React, { Component } from 'react';
import './App.css';
import PrivateRoute from './components/PrivateRoute';
import Login from './components/Login';
import { Route, Switch, Redirect } from "react-router-dom"
import Signup from './components/Signup';
import Favorites from "./components/Favorites";


const recipesUrl = "http://localhost:3000/recipes/";
const favoritesUrl = "http://localhost:3000/user_recipes/"


export default class App extends Component {

  state = {
    favorites: [],
    recipes: []
  }

  componentDidMount(){
    this.getRecipes();
    // this.getFavorites()
  }

  // getFavorites = () => {
  //   fetch(favoritesUrl)
  //   .then(response => response.json())
  //   .then(response => this.setState({
  //     favorites: response.results
  //   }))
  // }

  getRecipes = () => {
    fetch(recipesUrl)
    .then(response => response.json())
    .then(response => this.setState({
      recipes: response.results
    }))
    // .then(console.log)
  }


  addRecipe = (recipe) => {
    this.setState({
      recipes: [...this.state.recipes, recipe]
    })

    fetch(recipesUrl, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify(recipe)
    })
  }

  // addFavorites = (recipeId, userId) => {
  //   this.setState({
  //     favorites: [...this.state.favorites, recipe]
  //   })

  //   fetch(recipesUrl, {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json"
  //     },
  //     body: JSON.stringify(recipe)
  //   })
  // }

  render(){
    return ( 
      <div className="App">
        <Switch>
          {/* <Route path="/" render={(props) => <Authenticate {...props} />} /> */}
          <Route path="/login" render={(props) => <Login {...props} />} />
          <Route path="/signup" render={(props) => <Signup {...props} />} />
          <PrivateRoute exact path="/" addToFavorites={this.addToFavorites}/>
          <Route render={() => <Redirect to="/" />} />
          <Route path="/favorites" render={(props) => <Favorites recipes={this.state.recipes} {...props} />} />
        </Switch>
      </div>
    );
  }
}
