import React, { Component } from 'react';
import MealList from "./components/MealList"
import './App.css';

const mealUrl = "`https://api.edamam.com/api/food-database/parser?app_id=ca747d07&app_key=5be7b142c0faea901956bd642929df69cf&ingr=${req.params.name}`";
const apiKey = "&app_key=5be7b142c0faea901956bd642929df69";
const apiId = "&app_id=1ba68463";
const maxTime = "&time=30";
const maxIngreds = `&ingr=10`;

export default class App extends Component {

  state = {
    meals: []
  }

  componentDidMount(){
    this.fetchRecipes();
  }

fetchRecipes = async (...ingredients) => {
    const mappedIngreds = ingredients
      .map((ingredient, idx) => {
        if (idx < ingredients.length - 1) {
          return ingredient + "+";
        } else {
          return ingredient;
        }
      })
      .join("");

    const url = `${mealUrl}${mappedIngreds}${maxIngreds}${maxTime}${apiId}${apiKey}`;
    const res = await fetch(url);
    const recipes = res.data;
    console.log(recipes);
    // addToList(recipes)
  };

  // fetchRecipes("zucchini", "broccoli", "carrots");

  render(){
    return (
      <div className="App">
        <h1>Meal Deal</h1>
        <form>
          <input name="q" placeholder="Type for search"/>
          <input type="submit" value="search"/>
        </form>
      </div>
    );
  }
}
