import React, { Component } from "react"
import NewUserForm from "./NewUserForm";

const usersUrl ="http://localhost:3000/users";
const loginUrl ="http://localhost:3000/login/";


export default class Login extends Component {

    state = {
        username: "",
        password: "",
        error: "",
        users: []
    }

    componentDidMount(){
        this.getUsers()
    }

    getUsers = () => {
        fetch(usersUrl, )
          .then(response => response.json())
          .then(users => this.setState({users}))
    }


    addUser = (user) => {
        this.setState({
          users: [...this.state.users, user]
        })

        let newUser = {
          ...user
        }

        fetch(usersUrl, {
          method: "POST",
          headers: {
            'Content-type': 'application/json'
          },
          body: JSON.stringify(newUser)
        })
            .then(response => response.ok ? this.props.history.push('/') : this.props.history.push('/login') )
    }

    handleSubmit = (event) => {
        event.preventDefault();

        fetch(loginUrl, {
            method: "POST",
            headers: {
                "Content-type" : "application/json"
            },
            body: JSON.stringify(this.state)
        })
            .then(response => {
                if (response.status === 200) {
                    this.setState({error: ""})
                    return response.json()
                } else if (response.status === 401){
                    throw new Error("Check your username or password")
                }
            })
            .then(result => {
                localStorage.setItem("token", result.token);
                this.props.setCats(result.cats)
            })
            .catch(error => this.setState({error: error.message}));
        }


    handleChange = (event) => {
        const {name, value} = event.target;
        this.setState({
            [name]: value
        })
    }

    render(){
        return (
            <>
                <form className="login-form" onSubmit={this.handleSubmit}>
                    <h1>Login</h1>
                    <label>Username</label>
                    <input name="username" id="username"value={this.state.username} onChange={this.handleChange}/>
                    <label>Password</label>
                    <input type="password" id="password" name="password" value={this.state.password} onChange={this.handleChange} />
                    <input type="submit" id="submit" value="Login"/>
                    { this.state.error ? <p>{this.state.error}</p> : null}
                    {/* { console.log(this.state.error)} */}

                </form>
                {/* <NewUserForm users={this.state.users} addUser={this.addUser} /> */}
            </>
        )
    }
}
